import React, { useState } from 'react'
import { useProductsContext } from '../hooks/useProductsContext'
import TinyCard from '../components/TinyCard'
import AddForm from '../components/AddForm'

const Profile = () => {

  const { productsData } = useProductsContext()

  const tinyCards = productsData.reduce((prev, i) => {
    if (i.username == 'LuluMania') {
      prev.push(<TinyCard obj={i} />)

    }
    return prev
  }
    //if its username is Lulumania
    //push it
    //else return prev

    , [])




  return (
    <div className='profile--main'>
      <div className='profile--sidebar'>
        <img src='..\images\profile-picture.jpg' className='profile--picture' />
        <h1>LuluMania</h1>
        <h4>Huge Lulu Fans</h4>
        <p>Member since 2020</p>
        <p>Avg. Rating: 5.0 (20)</p>
      </div>

      <div className='profile--main--right'>

        <h2 className='profile--main--right--top'>Your Products:</h2>
        <div className='tinycard-container'>
          {tinyCards}

        </div>
        {/* beginning of form */}
        <AddForm />
        {/* end of form */}}

      </div>
    </div >
  )
}

export default Profile
