import React from 'react'
import Card from '../components/Card'
import ProductSideBar from '../components/ProductSideBar'
import { useProductsContext } from '../hooks/useProductsContext'

const ProductPage = () => {

    const {products} = useProductsContext()
    const [starSelector, setStarSelector] =React.useState([])
    return (
        <div className='main'>
            <div className='sidebar'>
                <ProductSideBar  starSelector={starSelector} setStarSelector={setStarSelector} />
            </div>

            <div className='card-container'>
            
                {/* {products.map((obj,index) => {
                    return <Card key={index} obj ={obj} />
                })} */}
                {products.reduce((prev,i,index)=>{
                    starSelector.includes(i.score.toFixed(0))&&
                    prev.push(<Card key={index} obj ={i}/>)
                    return prev
                },[])}

            </div>

        </div>
    )

}

export default ProductPage
