import React from 'react'
import { useState } from 'react';
import { useProductsContext } from '../hooks/useProductsContext';

const AddForm = () => {

    const { dispatch } = useProductsContext()
    const itemss = useProductsContext()

    const handleSubmit = event => {
        event.preventDefault();
        // handle form submission here


        const newItem = {
            id: Math.random() * 200000,
            img: inputImg,
            productname: inputProductName,
            username: inputUsername,
            score: 4.9,
            price: inputPrice,
        }
        dispatch({ type: 'CREATE_PRODUCT', payload: newItem })

    };

    const [inputImg, setImg] = useState('Product_10_Lulu_Student.webp');
    const [inputProductName, setProductName] = useState("Item Name");
    const [inputUsername, setUsername] = useState("LuluMania");
    const [inputPrice, setInputPrice] = useState(200);
  
    const items = ['Product_10_Lulu_Student.webp', 'Product_11_fitness_Lulu.jpg', 'Product_1_Wukong.webp', 'Product_2_flamingo_floatie.jpg', 'Product_3_Birthday_Cake.jpg', 'Product_4_Can_of_3.jpg', 'Product_5_Party_Lulu.jpg', 'Product_6_Can_Of_6.jpg', 'Product_7_Lulu_Sheep.jpg', 'Product_8_Lulu_Lite.jpg', 'Product_9_Sakura_Lulu.jpg', '12_Clown_Lulu_Plush.webp', '13_Delicious_Lulu_Plush.webp', '14_Party_Lulu_Plush.webp', '15_Sitting_Lulu_Plush.webp', '16_Lulu_T_Shirt.webp', '17_Lulu_Tote_Bag.webp', '18_Lulu_Umbrella.webp', '19_Lulu_Door_Curtain.webp', '20_Lulu_Memo_Pack.webp', '21_Lulu_Paper_Clips.webp', '22_Lulu_3D_Magnet.webp', '23_Lulu_Bee_3D_Magnet.webp']

    return (
        <div>
            <div className='profile--main--right--bottom'>
                <div>
                    <p>
                        Product Img Preview
                    </p>


                    <img src={`../images/${inputImg}`} className='product-preview' />
                </div>
                <div >
                    <h1>Add Your New Item Now</h1>

                    {/* Form */}
                    <form className="add-item-form">
                        <label>
                            <p>Product Image:</p>
                            <select
                                value={inputImg}
                                onChange={e => setImg(e.target.value)} className='add-item-input'>
                                {items.map((item, index) => <option key={index} value={item}>{item}</option>)}
                            </select>
                        </label>

                        <label>
                            <p>Item Name:</p>
                            <input
                                type="text"
                                value={inputProductName}
                                onChange={e => setProductName(e.target.value)}
                                className='add-item-input'

                            />
                        </label>
                        <label>
                            <p>Price:</p>
                            <input
                                type="number"
                                value={inputPrice}
                                onChange={e => setInputPrice(e.target.value)}
                                className='add-item-input'
                            />
                        </label>
                        <label>
                            <p>Username:</p>
                            <input
                                type="text"
                                value={inputUsername}
                                onChange={e => setUsername(e.target.value)}
                                className='add-item-input'
                            />
                        </label>

                 
                    </form>
                    <button className="add-item-submit"
                        onClick={handleSubmit}>List Your Item</button>
                </div>
            </div >
        </div >
    )
}

export default AddForm
