import React from 'react'

const ProductSideBar = ({ starSelector, setStarSelector }) => {
    const handleFilterChange = (e) => {
        const { value } = e.target;
        if (starSelector.includes(value)) {
            setStarSelector(starSelector.filter((f) => f !== value));
        } else {
            setStarSelector([...starSelector, value]);
        }
    };
    return (
        <div className='productsidebar' >
            <h3>Department</h3>
            <p>Stuffed Animals & Plush Toys</p>
            <p>Blind Boxes</p>
            <p>Accessories</p>
            <p>Limited Edition</p>
            <p>On Sale</p>


            <h3>Customer Review</h3>
            <p>⭐⭐⭐⭐⭐</p>
            <p>⭐⭐⭐⭐</p>
            <p>⭐⭐⭐</p>
            <p>⭐⭐</p>
            <p>⭐</p>
            <input
                type="checkbox"
                value={5}
                checked={setStarSelector.includes(5)}
                onChange={handleFilterChange}
            /> 5 Stars
            {/*
                < input
                type="checkbox"
            value={4}
            checked={setStarSelector.includes(4)}
            onChange={handleFilterChange}
            /> 4 Stars

            <input
                type="checkbox"
                value={3}
                checked={setStarSelector.includes(3)}
                onChange={handleFilterChange}
            /> 3 Stars */}
            <h3>Price Range</h3>
            <p>Under $25</p>
            <p>$25 to $50</p>
            <p>$50 to $100</p>
            <p>$100 to $200</p>
            <p>$200 & Above</p>

        </div>
    )
}

export default ProductSideBar
