import React from 'react'
import { useState } from 'react';

const AddForm = () => {


    const handleSubmit = event => {
        event.preventDefault();
        // handle form submission here
        console.log(
            {
                img: 'Product_10_Lulu_Student.webp',

                productname: 'Lulu Student',
                score: 4.9,
                username: 'SuperLulu',
                price: 295,
                id: 1000
            }
        )
    };

    const [inputImg, setImg] = useState("");
    const [inputProductName, setProductName] = useState("");
    const [inputUsername, setUsername] = useState("LuluMania");

    return (
        <div>
            <div className='profile--main--right--bottom'>
                <div>
                    <p>
                        Product Img Preview
                    </p>
                </div>
                <div className="add-item-form">

                    <h1>Add Your New Item Now</h1>
                    {/* Form */}
                    <form >
                        <p>Product Image:</p>
                        <input

                            type="text"
                            value={inputImg}
                            onChange={e => setImg(e.target.value)}
                            className='add-item-input'
                        />

                        <p>Product Name:</p>
                        <input
                            type="text"
                            value={inputProductName}
                            onChange={e => setProductName(e.target.value)}
                            className='add-item-input'
                        />
                        <p>Username:</p>
                        <input
                            type="text"
                            value={inputUsername}
                            onChange={e => setUsername(e.target.value)}
                            className='add-item-input'
                        />


                    </form>
                    <button className="add-item-submit"
                        onClick={handleSubmit}>List Your Item</button>
                </div>
            </div>
        </div>
    )
}

export default AddForm
